CREATE EXTENSION IF NOT EXISTS postgis;
CREATE EXTENSION IF NOT EXISTS pgrouting;
CREATE TABLE IF NOT EXISTS bibliometros (estacion TEXT NOT NULL, ubicacion TEXT, tipo TEXT, linea TEXT NOT NULL, PRIMARY KEY (estacion, linea));
CREATE TABLE IF NOT EXISTS conexiones (estacion_origen TEXT NOT NULL, estacion_destino TEXT, estado INTEGER NOT NULL, PRIMARY KEY (estacion_origen, estacion_destino));
CREATE TABLE IF NOT EXISTS lineas_estacion (estacion TEXT NOT NULL, linea TEXT NOT NULL);
CREATE TABLE IF NOT EXISTS coordenadas (estacion TEXT NOT NULL, longitud REAL NOT NULL, latitud REAL NOT NULL, PRIMARY KEY (estacion));
